import data from './data';
import Options from './components/Options';
import React from "react";

export default function App() {

  const opt = data.map((item) => {
    return <Options key={item.id} item={item} />;
  });

  const [showOpts, setShowOpts] = React.useState(false)

  function showOptions() {
    console.log("clicked")
    setShowOpts()

  }
  return (
    <div className="App">
      <h1>Coding Ninjas - React Project - Dropdown</h1>
      <h3>Should you use a dropdown?</h3>
      <div className="container">
        <div className="select" onMouseOver={showOptions}>
          <p>Select</p>
          <img
            src="https://cdn-icons-png.flaticon.com/128/2985/2985151.png"
            className="drop-icon"
          />
        </div>
        {/* options */}
        <div className="options">
          {opt}
        </div>
      </div>
    </div>
  );
}
