import './App.css';

function App() {
  return (
    <div className="App">
      <h1>Coding Ninjas - React Project - Dropdown</h1>
      <h3>Should you use a dropdown?</h3>
      <div className="container">
        <div className="select">
          <p>Select</p>
          <img src="https://cdn-icons-png.flaticon.com/128/2985/2985151.png" className="drop-icon" />
        </div>
        {/* options */}
        <div className="options">
          <div className="option">
            <p>Yes</p>
          </div>
          <div className="option">
            <p>Probably Not</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
